package bai_tap_them.quan_ly_hoa_don_tien_dien.controller;import java.util.Scanner;public class MainController {    private static final Scanner scanner = new Scanner(System.in);    public static void showMenu() {        int choice = 0;        do {            System.out.println("1. Thêm mới khách hàng\n" +                    "2. Hiện thị thông tin khách hàng\n" +                    "3. Tìm kiếm khách hàng\n" +                    "4. Thêm mới hóa đơn\n" +                    "5. Chỉnh sửa hóa đơn\n" +                    "6. Hiện thị thông  tin chi tiết hóa đơn\n" +                    "7. Thoát");            try {                choice = Integer.parseInt(scanner.nextLine());                if (choice > 7) {                    System.out.println("Vui lòng nhập lại");                }            } catch (Exception e) {                System.out.println("Vui lòng nhập chữ số");                continue;            }            switch (choice) {                case 1:                    addCustomer();                    break;                case 2:                    CustomerController.showListCustomer();                    break;                case 3:                    CustomerController.findByName();                    break;                case 4:                    InvoiceController.addInvoice();                    break;                case 5:                    InvoiceController.updateInvoice();                    break;                case 6:                    InvoiceController.showListInvoice();                    break;            }        } while (choice != 7);    }    public static void addCustomer() {        int choice = 0;        do {            System.out.println("1. Thêm mới khách hàng VN\n" +                    "2. Hiện thị thông tin khách hàng nước ngoài\n" +                    "3. Thoát");            try {                choice = Integer.parseInt(scanner.nextLine());                if (choice > 3) {                    System.out.println("Vui lòng nhập lại");                }            } catch (Exception e) {                System.out.println("Vui lòng nhập chữ số");                continue;            }            switch (choice) {                case 1:                    CustomerController.addLocalCustomer();                    break;                case 2:                    CustomerController.addForeignCustomer();                    break;            }        } while (choice != 3);    }    public static void main(String[] args) {        showMenu();    }}